import React, { Component, Fragment } from "react";
import config from "../../app/config";
import {store} from "../../redux/storeConfig/store";
import ContentHeader from "../../components/contentHead/contentHeader";
import {Alert, Button, Card, CardBody, CardTitle, Col, FormGroup, Input, Label, Row} from "reactstrap";
import {CheckSquare, FileText, Info, Mail, X, Map} from "react-feather";
import {Field, Formik, Form} from "formik";
import * as Yup from "yup";
import Select from 'react-select';
import "react-select/dist/react-select";

const formSchema = Yup.object().shape({
    name: Yup.string().required("Required").max(50, "Role name is too long.")
});

class Edit extends Component {
    constructor() {
        super();

        this.state = {
            auth: store.getState().authentication.Auth,
            formValues: {
                name: '',
                permissions: []
            },
            multivalues: [
            ],
            permissions: [],
            alert: {
                display: false,
                type: "success",
                message: ""
            }
        };
    }




    componentWillMount() {
        this.getRole();
        this.getPermissions();
    }

    getPermissions = async () => {
        const { permissions } = this.state;
        const requestOptions = {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                "Authorization": `Bearer ${this.state.auth.token}`
            }
        };
        fetch(`${config.base_url}v1/acl/permission`, requestOptions)
            .then(this.handleResponse)
            .then(response => {
                if(response.success === true){
                    const alert = {
                        type: "success",
                        message: response.message,
                        display: true
                    };
                    const {permissions} = response.data;
                    let statePermission = [];
                    permissions.map( permission => (
                        statePermission.push({
                            value: permission.id,
                            label: permission.name
                        })
                    ));
                    this.setState({permissions: statePermission });
                }
                return response;
            });
    };

    getRole = async () => {
        const { id } = this.props.match.params;
        const { formValues } = this.state;
        const requestOptions = {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                "Authorization": `Bearer ${this.state.auth.token}`
            }
        };
        fetch(`${config.base_url}v1/acl/role/${id}`, requestOptions)
            .then(this.handleResponse)
            .then(response => {
                if(response.success === true){
                    const alert = {
                        type: "success",
                        message: response.message,
                        display: true
                    };
                    let formValues = response.data.role;
                    this.setState({formValues });
                    let multivalues = [];
                    formValues.permissions.map(permission => {
                        const value = {value: permission.id, label: permission.name};
                        multivalues.push(value);
                    });
                    this.setState({multivalues});
                }
                else{
                    const alert = {
                        type: "danger",
                        message: response.error.message,
                        display: true
                    };
                    this.setState({alert});
                }
                return response;
            });
    };

    updateRole = async () => {
        const { id } = this.props.match.params;
        let { formValues, multivalues } = this.state;
        formValues.permissions = [];
        multivalues.map(val => {
            formValues.permissions.push(val.value);
        });
        const requestOptions = {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                "Authorization": `Bearer ${this.state.auth.token}`
            },
            body: JSON.stringify(formValues)
        };
        fetch(`${config.base_url}v1/acl/role/${id}`, requestOptions)
            .then(this.handleResponse)
            .then(response => {
                if(response.success === true){
                    const alert = {
                        type: "success",
                        message: response.message,
                        display: true
                    };
                    this.setState({alert});
                }
                else{
                    const alert = {
                        type: "danger",
                        message: response.error.message,
                        display: true
                    };
                    this.setState({alert});
                }
                return response;
            });
    };

    handleResponse(response) {
        return response.text().then(text => {
            return text && JSON.parse(text);
        });
    }

    handleSubmit = e => {
        this.updateRole();
    };

    handleMultiChange = option => {
        this.setState({multivalues: option});
    };

    handleResetForm = () => {
        document.getElementById("vehicleForm").reset();
    };

    findPermission(id){
        let {formValues} = this.state;
        let obj = formValues.permissions.find(p => p.id === id);
        if(obj) {
            if(formValues.addPermission.indexOf(id === -1)){
                formValues.addPermission.push(id);
            }
            return true;
        }
        return false;
    }

    render() {
        const {permissions, formValues} = this.state;
        return (
            <Fragment>
                <ContentHeader>Vehicles Update </ContentHeader>

                <Row>
                    <Col md="12">
                        <Card>
                            <CardBody>
                                <div className="px-3">
                                    <Formik
                                        initialValues={this.state.formValues}
                                        enableReinitialize={true}
                                        validationSchema={formSchema}
                                        onSubmit={(data, {setSubmitting}) => {
                                            setSubmitting(true);
                                            this.setState({formValues: data});
                                            this.handleSubmit();
                                            setSubmitting(false);
                                        }}
                                    >
                                        {({ values, isSubmitting, errors, touched, handleChange}) => (
                                    <Form id="vehicleForm">
                                        <div className="form-body">
                                            <h4 className="form-section"><Map size={20} color="#212529" /> Role Info</h4>
                                            <Row>
                                                <Col md="12">
                                                    <FormGroup>
                                                        <Label for="name">Role Name</Label>
                                                        <Field name="name" id="name" value={values.name} className={`form-control ${errors.name && touched.name && 'is-invalid'}`}/>
                                                        {errors.name && touched.name ? <div className="invalid-feedback">{errors.name}</div> : null}
                                                    </FormGroup>
                                                </Col>
                                            </Row>

                                            <Row>
                                                <Col md="12">
                                                    <Select
                                                        options = {permissions}
                                                        value={this.state.multivalues}
                                                        onChange={this.handleMultiChange}
                                                        isMulti={true}  />
                                                </Col>
                                            </Row>

                                        </div>

                                        <div className="form-actions">
                                            <Button color="primary" type="submit">
                                                <CheckSquare size={16} color="#FFF" /> Save
                                            </Button>
                                            {   this.state.alert.display &&
                                                <Alert color={this.state.alert.type} >
                                                    {this.state.alert.message}
                                                </Alert>
                                            }


                                        </div>
                                    </Form>

                                    )}
                                    </Formik>
                                </div>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Fragment>
        );
    }
}

export default Edit;
